using System;using Newtonsoft.Json;namespace PagarmeClient{    public class Card : Base.Model    {        protected override string Endpoint { get { return "/cards"; } }        public string CardHash        {            get { return GetAttribute<string>("card_hash"); }            set { SetAttribute("card_hash", value); }        }        public string Number        {            get { return GetAttribute<string>("number"); }            set { SetAttribute("number", value); }        }        public string ExpirationDate        {            get { return GetAttribute<string>("expiration_date"); }            set { SetAttribute("expiration_date", value); }        }        public string Cvv        {            get { return GetAttribute<string>("cvv"); }            set { SetAttribute("cvv", value); }        }        public CardBrand Brand        {            get { return GetAttribute<CardBrand>("brand"); }            set { SetAttribute("brand", value); }        }        public string HolderName        {            get { return GetAttribute<string>("holder_name"); }            set { SetAttribute("holder_name", value); }        }        public string FirstDigits        {            get { return GetAttribute<string>("first_digits"); }            set { SetAttribute("first_digits", value); }        }        public string LastDigits        {            get { return GetAttribute<string>("last_digits"); }            set { SetAttribute("last_digits", value); }        }        public string Fingerprint        {            get { return GetAttribute<string>("fingerprint"); }            set { SetAttribute("fingerprint", value); }        }        public Customer Customer        {            get { return GetAttribute<Customer>("customer"); }            set { SetAttribute("customer", value); }        }        public bool Valid        {            get { return GetAttribute<bool>("valid"); }            set { SetAttribute("valid", value); }        }        public Card()            : this(null)        {        }        public Card(PagarMeService service)            : base(service)        {        }    }}

