using System;namespace PagarmeClient{    public class Phone : Base.AbstractModel    {        public string Id        {            get            {                var result = GetAttribute<object>("id");                if (result == null)                    return null;                return result.ToString();            }            set { SetAttribute("id", value); }        }        public string Ddi        {            get { return GetAttribute<string>("ddi"); }            set { SetAttribute("ddi", value); }        }        public string Ddd        {            get { return GetAttribute<string>("ddd"); }            set { SetAttribute("ddd", value); }        }        public string Number        {            get { return GetAttribute<string>("number"); }            set { SetAttribute("number", value); }        }        public Phone()            : this(null)        {        }        public Phone(PagarMeService service)            : base(service)        {        }    }}

