using System;using Newtonsoft.Json;namespace PagarmeClient{	public class Recipient : Base.Model	{		protected override string Endpoint { get { return "/recipients"; } }		public BankAccount BankAccount		{			get { return GetAttribute<BankAccount>("bank_account"); }			set { SetAttribute("bank_account", value); }		}		public bool TransferEnabled		{			get { return GetAttribute<bool>("transfer_enabled"); }			set { SetAttribute("transfer_enabled", value); }		}		public DateTime LastTransfer		{			get { return GetAttribute<DateTime>("last_transfer"); }			set { SetAttribute("last_transfer", value); }		}		public TransferInterval TransferInterval		{			get { return GetAttribute<TransferInterval>("transfer_interval"); }			set { SetAttribute("transfer_interval", value); }		}		public int TransferDay		{			get { return GetAttribute<int>("transfer_day"); }			set { SetAttribute("transfer_day", value); }		}		public Recipient()			: this(null)		{		}		public Recipient(PagarMeService service)			: base(service)		{		}	}}
