using System;namespace PagarmeClient{	public class SplitRule : Base.AbstractModel	{		public string Id		{			get			{				var result = GetAttribute<object>("id");				if (result == null)					return null;				return result.ToString();			}			set { SetAttribute("id", value); }		}		public Recipient Recipient		{			get { return GetAttribute<Recipient>("recipient"); }			set { SetAttribute("recipient", value); }		}		public bool ChargeProcessingFee		{			get { return GetAttribute<bool>("charge_processing_fee"); }			set { SetAttribute("charge_processing_fee", value); }		}		public bool Liable		{			get { return GetAttribute<bool>("liable"); }			set { SetAttribute("liable", value); }		}		public float Percentage		{			get { return GetAttribute<float>("percentage"); }			set { SetAttribute("percentage", value); }		}		public int Amount		{			get { return GetAttribute<int>("amount"); }			set { SetAttribute("amount", value); }		}		public SplitRule()			: this(null)		{		}		public SplitRule(PagarMeService service)			: base(service)		{		}	}}

